from NVDAObjects.UIA import UIAclass avpuia(UIA):	def _get_children(self):		l = list()		if self.firstChild:			obj = self.firstChild			while obj not in l:				l.append(obj)				try:					if obj.next:						obj = obj.next				except:					pass		return lclass avptablecell(avpuia):	def _get_name(self):		l = list()		for x in self.children:			childname = x.name			childrole = x.role			if childname and childrole == controlTypes.ROLE_STATICTEXT:				l.append(childname) 		return ', '.encode('utf-8').join(l)	def _get_columnHeaderText(self):		colname1 = self.parent.parent.firstChild.getChild(self.columnNumber -1).name		if  colname1 != u'':			s =  colname1		else:			try:				colname2 =  self.parent.parent.firstChild.getChild(self.columnNumber -1).lastChild.previous.name			except:				colname2 = ''			s = colname2		return sclass avpcheckbox(avpuia):	def _get_name(self):		l = list()		for x in self.children:			childname = x.name			if childname != '':				l.append(childname)		if len(l) == 0:			l.append(self.next.name)		return '; '.join(l)class avplistitem(avpuia):	def _get_name(self):		l = list()		if self.firstChild.role == controlTypes.ROLE_UNKNOWN:			obj = self.firstChild		else:			obj = self		for x in obj.children:			childname = x.name			childrole = x.role			if childrole == controlTypes.ROLE_STATICTEXT:				l.append(childname)		return '; '.join(l)class avpmenuitem(avpuia):	def _get_name(self):		s = ''		try:			name = self.name		except:			name = ''		try:			childname = self.firstChild.name		except:			childname = ''		if name != '':			s = name		elif childname != '':			s = childname		return sclass  avpunlabeledbutton(avpuia):
	def _get_name(self):
		uiaid = self.UIAElement.CurrentAutomationId		uianame = self.UIAElement.CurrentName		if uianame != '':			s = uianame		elif uiaid != '':				s = uiaid		else:			l = list()			for x in self.children:				childname = x.name				if childname != '':					l.append(childname)			try:				s = l[0]			except:				s = ''		return s
	def _get_description(self):
		uiadesc = self.UIAElement.CurrentHelpText		if uiadesc != '':			s = uiadesc		else:			l = list()			for x in self.children:				childname = x.name				if childname != '':					l.append(childname)			try:				s = l[1]			except:				s = ''		return sclass avpdataitem(avpuia):	def _get_name(self):		l = list()		for x in self.children:			childname = x.name			childheader = x.columnHeaderText			if childname:				s = ''				if childheader:					s += childheader+': '				s += childname				l.append(s) 		return '; '.encode('utf-8').join(l)class AppModule(appModuleHandler.AppModule):	def chooseNVDAObjectOverlayClasses(self, obj, clslist):
		if isinstance(obj, UIA):			if obj.UIAElement.CurrentFrameworkId == 'WPF':				if obj.role == controlTypes.ROLE_BUTTON:
					clslist.insert(0,avpunlabeledbutton)
				elif obj.role == controlTypes.ROLE_DATAITEM:					clslist.insert(0, avpdataitem)				elif obj.role == controlTypes.ROLE_LISTITEM:					clslist.insert(0, avplistitem)				elif obj.role == controlTypes.ROLE_MENUITEM:					clslist.insert(0,avpmenuitem)				elif obj.parent.role == controlTypes.ROLE_DATAITEM and obj.UIAElement.CurrentClassName == 'ContentPresenter':					clslist.insert(0,avptablecell)				elif obj.role == controlTypes.ROLE_CHECKBOX and obj.name == u'':					clslist.insert(0,avpcheckbox)